<?phpnamespace backend\modules\postmanager\controllers;use Yii;use soft\web\SController;use backend\modules\postmanager\models\Post;use backend\modules\postmanager\models\search\PostSearch;/** * PostController implements the CRUD actions for Post model. */class PostController extends SController{    public function actionIndex()    {        $searchModel = new PostSearch();        $dataProvider = $searchModel->search(Yii::$app->request->queryParams);        return $this->render('index', [            'searchModel' => $searchModel,            'dataProvider' => $dataProvider,        ]);    }    public function actionView($id)    {        return $this->render('view', [            'model' => Post::findModel($id),        ]);    }    public function actionCreate()    {        $model = new Post();        $model->publishedField = date("Y-m-d H:i");        if ($model->load(Yii::$app->request->post()) ) {            $model->published_at = date('Y-m-d');            $model->tags = trim(implode(';', $model->tagsField));            if($model->save()){                return $this->redirect(['view', 'id' => $model->id]);            }        }        return $this->render('create', [            'model' => $model,        ]);    }    public function actionUpdate($id)    {        $model = Post::findModel($id);        $model->tagsField = explode(';', $model->tags);        $model->publishedField = date("Y-m-d H:i", $model->published_at);        if ($model->load(Yii::$app->request->post()) ) {            $model->published_at = strtotime($model->publishedField);            $model->tags = trim(implode(';', $model->tagsField));            if($model->save()){                return $this->redirect(['view', 'id' => $model->id]);            }        }        return $this->render('update', [            'model' => $model,        ]);    }    public function actionDelete($id)    {        $this->findModel($id)->delete();        return $this->redirect(['index']);    }}