<?phpnamespace backend\modules\postmanager\models\search;use Yii;use yii\base\Model;use yii\data\ActiveDataProvider;use backend\modules\postmanager\models\Review;class ReviewSearch extends Review{    public function rules()    {        return [            [['id', 'created_at', 'updated_at', 'user_id', 'product_id'], 'integer'],            [['slug'], 'safe'],            [['status'], 'boolean'],        ];    }    public function scenarios()    {        return Model::scenarios();    }    public function search($params)    {        $query = Review::find()->with('product')->recently();        $dataProvider = new ActiveDataProvider([            'query' => $query,        ]);        $this->load($params);        if (!$this->validate()) {            return $dataProvider;        }        $query->andFilterWhere([            'id' => $this->id,            'created_at' => $this->created_at,            'updated_at' => $this->updated_at,            'status' => $this->status,            'user_id' => $this->user_id,            'product_id' => $this->product_id,        ]);        $query->andFilterWhere(['like', 'slug', $this->slug]);        return $dataProvider;    }}