<?phpuse yii\helpers\Html;use yii\helpers\ArrayHelper;use soft\kartik\SActiveForm;use mihaildev\elfinder\ElFinder;use mihaildev\ckeditor\CKEditor;mihaildev\elfinder\Assets::noConflict($this);?><div class="post-form">    <?php $form = SActiveForm::begin(); ?>    <?= $form->languageSwitcher($model); ?>    <div class="row">      <div class="col-md-9">         <?= $form->field($model, 'title')->textInput() ?>          <?= $form->field($model, 'content')->widget(CKEditor::className(), [              'editorOptions' => ElFinder::ckeditorOptions('elfinder', []),              ])?>      </div>      <div class="col-md-3">          <?= $form->field($model, 'category_id')->dropDownList(ArrayHelper::map(backend\modules\postmanager\models\PostCategory::find() ->all(), 'id', 'title'))->label(Yii::t('app', 'Category')) ?>          <?= $form->field($model, 'image')->widget(\mihaildev\elfinder\InputFile::className(), [            'language'      => Yii::$app->language,            'controller'    => 'elfinder',            'filter'        => 'image',                'template'      => '<div class=input-group>{input}<span class=input-group-btn>{button}</span></div>',            'options'       => ['class' => 'form-control'],            'buttonOptions' => ['class' => 'btn btn-default'],            'multiple'      => false,              'id' => 'selected'        ])->label(Yii::t('app', 'Image')) ?>        <?= Html::img($model->image, ['width' => '90%']) ?>        <br><br>          <?php echo $form->field($model, 'status')->widget(\kartik\widgets\SwitchInput::classname(), [              'pluginOptions' => [                  'onText' => Yii::t('app', 'Active'),                  'offText' => Yii::t('app', 'Inactive'),              ],          ]); ?>          <div class="form-group">            <?= Html::submitButton(Yii::t('app', 'Save'), ['class' => 'btn btn-success']) ?>        </div>      </div>