<?phpuse yii\helpers\Html;use soft\grid\SKGridView;/* @var $this yii\web\View *//* @var $searchModel common\models\search\PostSearch *//* @var $dataProvider yii\data\ActiveDataProvider */$this->title = Yii::t('app', 'Posts');$this->params['breadcrumbs'][] = $this->title;$categoriesFilter =  yii\helpers\ArrayHelper::map(backend\modules\postmanager\models\Post::find()->with('category')->all(), 'category_id', 'category.title');?><div class="post-index">    <h1><?= Html::encode($this->title) ?></h1>    <p>        <?= Html::a(Yii::t('app', 'Create Post'), ['create'], ['class' => 'btn btn-success']) ?>    </p>    <?= SKGridView::widget([        'dataProvider' => $dataProvider,        'filterModel' => $searchModel,        'cols' => [            'title',              [                'attribute'=>'category_id',                'value' => function($model){                    return $model->category->title;                },                'filter' => $categoriesFilter,            ],            'status',            'updated_at',            'published_at',            'actionColumn',        ],    ]); ?></div>