<?phpnamespace backend\modules\translationmanager;use Yii;use yii\helpers\ArrayHelper;use yii\helpers\Html;/** * translation module definition class */class TranslationManager extends \yii\base\Module{  /**   * @var array Array of languages   */    public $languages = ['uz', 'ru', 'kr'];    public $grid_column = [];    /**     * @inheritdoc     */    public $controllerNamespace = 'backend\modules\translationmanager\controllers';    /**     * @inheritdoc     */    public function init()    {        parent::init();        if (isset(Yii::$app->params['languages'])){            $langs = Yii::$app->params['languages'];            if (is_array($langs)){                $this->languages = array_keys($langs);            }        }        $this->grid_column = $this->getGridColumns();    }  /**   * @return array Return array of column for gridViewWidget   */    public function getGridColumns(){        $columns = [            ['class' => 'yii\grid\SerialColumn'],//            'category',            'message:ntext'        ];        foreach ($this->languages as $one){            $columns[] = [                'label' => Yii::t('app',  $one),                'value' => 'languages.'.$one,                'filter' => '<input type="text" class="form-control" value="'.$_GET['SourceMessageSearch']['languages'][$one].'" name="SourceMessageSearch[languages]['.$one.']">',            ];        }        $columns[] = [            'class' => 'kartik\grid\ActionColumn',            'width' => "150px",            'viewOptions' => ['class' => 'btn btn-sm btn-info'],            'updateOptions' => ['class' => 'btn btn-sm btn-success'],            'deleteOptions' => ['class' => 'btn btn-sm btn-danger'],        ];        return $columns;    }}