<?php    $totalPrice = 0;?><table class="table table-bordered">    <tr>        <td colspan="100%">            <h4 class="text-sussess">                <?= t('Ordered items') ?>                <span class="badge">                    <?= count($model->orderProducts) ?>                </span>            </h4>        </td>    </tr>    <tr>        <th>Rasmi</th>        <th>Nomi</th>        <th>Narxi</th>        <th>Soni</th>        <th>Umumiy narx</th>    </tr>    <?php foreach ($model->orderProducts as $item): ?>        <tr>            <td width="15%">                <img src="<?= $item->product->image ?>" alt="" style="max-height:75px;max-width:75px;display: block;margin-left: auto;margin-right: auto;">            </td>            <td><?= $item->product->name ?></td>            <td><?= Yii::$app->formatter->asSum($item->product->sale_price) ?></td>            <td><?= $item->quantity ?></td>            <?php                $price = $item->product->sale_price * $item->quantity;                $totalPrice += $price;            ?>            <td><?= Yii::$app->formatter->asSum( $price ) ?></td>        </tr>    <?php endforeach; ?>        <tr class="h4">        <th colspan="2">Umumiy narx:</th>        <th colspan="3">            <span class="pull-right">                <?= Yii::$app->formatter->asSum($totalPrice) ?>            </span>        </th>    </tr></table>