<?phpnamespace soft\behaviors;use Yii;use yii\base\Behavior;use yii\db\ActiveRecord;class SAuthorBehavior extends Behavior{    public $createdByAttribute = 'user_id';    public function events()    {        return [            ActiveRecord::EVENT_BEFORE_INSERT => 'beforeInsert',        ];    }    public function beforeInsert($event)    {        $createdByAttribute = $this->createdByAttribute;        if ($this->owner->hasAttribute($createdByAttribute) && !Yii::$app->user->isGuest )        {            $this->owner->$createdByAttribute = Yii::$app->user->identity->id;        }    }}