<?php 	namespace soft\db;    use yeesoft\multilingual\db\MultilingualTrait;    use yii\data\Pagination;    use yii\db\ActiveRecord;    class SActiveQuery extends \yii\db\ActiveQuery	{		use MultilingualTrait;		public function active()        {		    return $this->andWhere([$this->tableName.'.status' => 1]);		}        public function deleted($deleted = true)        {            return $this->andWhere([$this->tableName.'.deleted' => $deleted]);		}        public function latest($limit=0)        {            $query = $this->addOrderBy([$this->tableName.'.created_at' => SORT_DESC]);            if ($limit > 0){                $query->limit($limit);            }            return $query;		}        public function paginate($defaultPageSize=20)        {            $pagination = new Pagination([                'defaultPageSize' => $defaultPageSize,                'totalCount' => $this->count()            ]);            $model = $this->offset($pagination->offset)                ->limit($pagination->limit)                ->all();            return [                'pagination' => $pagination,                'model' => $model,            ];		}        public function id($id='')        {            return $this->andWhere(['id' => $id]);        }        public function getTableName()        {            /* @var $modelClass ActiveRecord */            $modelClass = $this->modelClass;            return $modelClass::tableName();        }	} ?>