<?php	// use Yii;	use soft\helpers\SHtml;	use soft\helpers\SArray;	function e($content = "", $doubleEncode = true)    {		    return SHtml::encode($content, $doubleEncode);	}	function de($content = "")    {		    return SHtml::decode($content);	}	function a($text, $url = null, $options = [], $icon = null)	{		return SHtml::a($text, $url, $options, $icon);	}	function back($url = null, $options=[], $icon = 'arrow-left' ){		if ($url == null) {			$url = Yii::$app->request->referrer;		}		SArray::setValueIfNoValid($options, 'class', 'btn btn-primary btn-sm');		return SHtml::a("Ortga", $url, $options, $icon);	}	function tag($name, $content = '', $options = [])    {        if ($name === null || $name === false) {            return $content;        }        return SHtml::tag($name, $content, $options);    }    function fa($icon=''){    	return SHtml::tag('i', "", [ 'class' => "fa fa-{$icon}"]);    }    function fas($icon=''){    	return SHtml::tag('i', "", [ 'class' => "fas fa-{$icon}"]);    }    function submit_button($label = "Saqlash", $options = [], $icon = 'save'){		SArray::setValueIfNoValid($options, 'class', 'btn btn-success');		$label = SHtml::withIcon($label, $icon);		return SHtml::submitButton($label, $options);    }