<?phpnamespace soft\web;use soft\behaviors\ChangeLanguage;use Yii;use soft\helpers\SArray;class SUrlManager extends \yii\web\UrlManager{    public function createUrl($params=[])    {        if ( is_array($params) && !isset($params['language'])){            $language = Yii::$app->request->get('language', Yii::$app->params['defaultLanguage']);            $params['language'] = $language;        }//        $params[0] = Yii::$app->controller->route;        return parent::createUrl($params);    }    public function absoluteUrl($url)    {        if (strpos($url, '://') === false) {            $url = 'http://' . $url;        }        return $url;    }   /* public function setLanguage()    {        $languageParam = SArray::getValue(Yii::$app->params, 'languageParam');        $language = Yii::$app->request->get($languageParam);        dd(Yii::$app->request->getQueryParams());        if ($language == null || !key_exists( $language, Yii::$app->params['languages']))        {            $language = Yii::$app->params['defaultLanguage'];        }        Yii::$app->language = $language;    }*/}