<?php namespace soft\web;use Yii;use johnitvn\ajaxcrud\CrudAsset;use yii\helpers\Html;use yii\web\View;use yii\widgets\Pjax;use yii\bootstrap\Modal;use soft\helpers\SArray;use yii\widgets\Breadcrumbs;    class SView extends View    {        use VCTrait;        public $metaImage;        public $breads=[];        public $header;        public $metaTitle;        public $metaDescription;        public $metaKeywords;        public $metaUrl;         public function registerAssetBundle($name, $position = null)        {            return parent::registerAssetBundle($name, $position);        }        public function pjaxBegin()        {            Pjax::begin($this->layoutSettings['pjaxOptions']);        }        public function pjaxEnd()        {            Pjax::end();        }        public function modalBegin($options)        {            Modal::begin($options);        }        public function modalEnd()        {            Modal::end();        }        public function renderModal($options=[])        {            $this->modalBegin($options);            $this->modalEnd();        }        public function renderCrudModal()        {            $options = [                "id"=>"ajaxCrudModal",                "footer" => '',            ];            $this->renderModal($options);        }        public function registerBreadcrumbs($options=[])        {            $options['links'] = !empty($this->breads) ? $this->breads : SArray::getValue( $this->params, 'breadcrumbs', []);            return Breadcrumbs::widget($options);        }        public function registerCrudAsset()        {            CrudAsset::register($this);        }        public function getRoute()        {            return Yii::$app->controller->route;        }        public function getCsrfInput()        {            $csrfParam = $this->request->csrfParam;            $csrfToken = $this->request->csrfToken;            return Html::hiddenInput($csrfParam, $csrfToken);        }    }